import React, { Component } from "react";

export default class Counter extends Component {

    // criando stado e pasando step local

    //state = {
    //     step: 1,
    //     valor: 0

    // }

    // recebendo o step do app
    constructor(props) {
        super(props);
        this.state = {
            passo: props.step,
            valor: 0,
        };
    }

    render() {
        return (
            <div>
                <h2> Contador </h2>
                <h4> Passo: {this.props.step}</h4>
                <h4> Valor: {this.state.props} </h4>

                <div>
                    <button>+</button>
                    <button>+</button>
                </div>
            </div>
        );
    }
}
